export function replaceRedditLinks(e){return e.replace(/href="\/u\/([^"]+)"/g,'href="user.html?u=$1"').replace(/href="\/r\/([^"]+)"/g,'href="subreddit.html?r=$1"').replace(/(href=\"https:\/\/(old.|www.|)reddit\.com\/r\/[^\/]+\/comments\/[^"]+)(\?[^"]+)?/g,function(e,r,t){return'class="comment-icon icon" href="comments.html?url='+encodeURIComponent(r).replace(/href%3D%22/g,"")}).replace(/(href=\"https:\/\/reddit\.com\/r\/[^\/]+\/comments\/[^"]+)(\?[^"]+)?/g,function(e,r,t){return'class="comment-icon icon" comments.html?url='+encodeURIComponent(r+(t||""))})}export function htmlDecode(e){return(new DOMParser).parseFromString(e,"text/html").body.textContent}